import React, { useState } from "react";

const CreateTweet = () => {
  let change = "Tweet";
  const [name, setchange] = useState("Dev Ed");
  const submit = (e) => {
    e.preventDefault();
    change = "post"; //! In React , this dosen't work
    console.log(change);

    setchange("Ed Dev");
  };
  return (
    <form>
      <h3>
        Create {change} {name}
      </h3>
      <textarea cols="50" rows="5"></textarea>
      <button onClick={submit}>Submit</button>
    </form>
  );
};

export default CreateTweet;
