import React, { useState, useEffect } from "react";
import TweetList from "./components/TweetList";
import CreateTweet from "./components/CreateTweet";

const Test = () => {
  //* Write a normal Javascript here
  /*
  const auther = "Zet Lewis";
  const message = "I want to sleep now wow!";

  return (
    ! return a Single Value
    <div>
      <h1>Hello</h1>
      <CreateTweet />
      <TweetList name={auther} message={message} />
      ! props flows upper to lower(components)
    </div>
  );
  */

  const [name, setName] = useState("Zet Lewis");
  const [textInput, setTextInput] = useState("");
  const [tweets, setTweets] = useState([]);
  const message = "I want to sleep now wow!";

  //! useEffect takes two parameters, first is function and
  //! second is function works this change : empty array is window refresh

  useEffect(() => {
    console.log("function running");
  }, []);

  return (
    <div>
      <h1>Hello</h1>
      <CreateTweet
        setTextInput={setTextInput}
        textInput={textInput}
        tweets={tweets}
        setTweets={setTweets}
      />
      <TweetList name={name} tweets={tweets} setTweets={setTweets}></TweetList>
    </div>
  );
};

export default Test;
