import React from "react";
import "./../style/app.css";

const Tweet = ({ auther, post, tweets, setTweets }) => {
  const deleteTweet = () => {
    console.log(post);
    setTweets(tweets.filter((tweet) => tweet.id !== post.id));
  };

  const s = { backgroundColor: "blue", color: "white", border: "none" };
  return (
    <div style={{ backgroundColor: "lightcoral" }}>
      <h1 className="auther">My name is {auther}</h1>
      <h2>{post.message}</h2>
      <button onClick={deleteTweet}>Delete</button>
      <button style={s}>Like</button>
    </div>
  );
};

export default Tweet;
