export const playAudio = (isPlaying, audio) => {
  if (isPlaying) {
    //console.log(audio.current.play())
    const playPromise = audio.current.play();
    if (playPromise !== undefined) {
      playPromise.then(() => {
        audio.current.play();
      });
    }
  }
};
