import React from "react";
import "./../style/app.css";
import m from "./../style/app.module.css";

const Tweet = ({ auther, post, tweets, setTweets }) => {
  const deleteTweet = () => {
    console.log(post);
    setTweets(tweets.filter((tweet) => tweet.id !== post.id));
  };

  const s = {
    backgroundColor: "blue",
    color: "white",
    border: "none",
    padding: "0.5rem 1rem",
    margin: "0rem 0.3rem",
  };
  /*
  ? Styling React Components
  * inlineStyle => style={{ backgroundColor: "lightcoral" }}
  * externalCss => className="auther" and import app.css
  * moduleStyle => className={m.module} and import app.module.css
  * JsStyle     => style={s} and define variable s with css
  */
  return (
    <div style={{ backgroundColor: "lightcoral" }} className="sass">
      <h1 className="auther">My name is {auther}</h1>
      <h2>{post.message}</h2>
      <button onClick={deleteTweet} className={m.module}>
        Delete
      </button>
      <button style={s}>Like</button>
    </div>
  );
};

export default Tweet;
