import React from "react";

const Tweet = ({ auther, post, tweets, setTweets }) => {
  const deleteTweet = () => {
    console.log(post);
    setTweets(tweets.filter((tweet) => tweet.id !== post.id));
  };
  return (
    <div>
      <h1>My name is {auther}</h1>
      <h2>{post.message}</h2>
      <button onClick={deleteTweet}>Delete</button>
      <button>Like</button>
    </div>
  );
};

export default Tweet;
