import React, { useState } from "react";

const CreateTweet = (props) => {
  const userInputHandler = (e) => {
    props.setTextInput(e.target.value);
  };
  const submit = (e) => {
    e.preventDefault();
    props.setTweets([...props.tweets, { message: props.textInput }]);
    props.setTextInput("");
  };
  return (
    <form onSubmit={submit}>
      <h3>Create Tweet</h3>
      <input onChange={userInputHandler} value={props.textInput}></input>
      <button>Submit</button>
    </form>
  );
};

export default CreateTweet;
