import React, { useState } from "react";

const CreateTweet = (props) => {
  let change = "Tweet";
  const [name, setchange] = useState("Dev Ed");

  const submit = (e) => {
    e.preventDefault();
    change = "post"; //! In React , this dosen't work
    console.log(change);

    setchange("Ed Dev"); //! IN React , use this state
    //* If state change , the component will change

    const test = document.querySelector("textarea");
    props.setname("test.value");
  };

  const [text, setText] = useState("");
  const userInputHandler = (e) => {
    setText(e.target.value);
  };
  return (
    <form>
      <h3>
        Create {change} {name}
      </h3>
      <textarea
        value={text}
        cols="50"
        rows="5"
        onChange={userInputHandler}
      ></textarea>
      <button onClick={submit}>Submit</button>
      <h1 onClick={() => setText("")}>{text}</h1>
    </form>
  );
};

export default CreateTweet;
